// --- CONFIGURATION ---
const MAX_PAGES = 64;
const ITEMS_PER_PAGE = 10;
const HERO_ITEMS_COUNT = 30; // Pages 1-3

// Helper to clean currency strings "$1,000.00" -> 1000.00
function cleanMoney(val) {
  if (typeof val === 'number') return val;
  if (!val) return 0;
  return parseFloat(val.toString().replace(/[$,%]/g, ''));
}

// 1. Process all incoming items
let allItems = items.map(item => {
  const json = item.json;

  // Calculate Scores (Simplified Normalization)
  // Note: For true normalization we'd need max values, but raw values work for sorting relative to each other
  const sales = cleanMoney(json['Promo Net Sales']);
  const margin = cleanMoney(json['Promo Margin %']);
  const lift = cleanMoney(json['Promo Lift Sales']);

  // Weighted Score: 50% Sales, 30% Margin, 20% Lift
  // We multiply margin by 100 to make it comparable to sales figures roughly, or just rely on the sort
  // Better approach for simple sort: Just weighted sum of raw values if scales differ wildly? 
  // Let's use a simplified "Power Score" for n8n without external libraries:
  const score = (sales * 0.5) + (lift * 0.2) + (margin * 1000 * 0.3); 

  // Infer Category if missing
  let cat = json['OFFICERS_CATEGORY'] || json['Category'] || 'General';
  const desc = (json['Item Description'] || '').toUpperCase();

  if (!json['OFFICERS_CATEGORY']) {
    if (desc.includes('BIBLE') || desc.includes('NIV') || desc.includes('ESV')) cat = 'BIBLES';
    else if (desc.includes('HC') || desc.includes('PB') || desc.includes('BOOK')) cat = 'BOOKS';
    else if (desc.includes('GIFT') || desc.includes('SET')) cat = 'GIFTS';
  }

  return {
    json: {
      ...json,
      clean_sales: sales,
      clean_category: cat,
      catalog_score: score
    }
  };
});

// 2. Filter valid items (Sales > 0)
allItems = allItems.filter(i => i.json.clean_sales > 0);

// 3. Global Sort by Score
allItems.sort((a, b) => b.json.catalog_score - a.json.catalog_score);

// 4. Limit to Top 640
const topItems = allItems.slice(0, MAX_PAGES * ITEMS_PER_PAGE);

// 5. Layout Strategy

// Part A: Heroes (Top 30 Global)
const heroes = topItems.slice(0, HERO_ITEMS_COUNT);
heroes.forEach((item, index) => {
  const page = Math.floor(index / ITEMS_PER_PAGE) + 1;
  item.json.Layout_Section = `Page ${page} (Hero/Best Seller)`;
});

// Part B: The Body (Remaining Items)
const body = topItems.slice(HERO_ITEMS_COUNT);

// Sort Body by Category -> Then Score
body.sort((a, b) => {
  if (a.json.clean_category < b.json.clean_category) return -1;
  if (a.json.clean_category > b.json.clean_category) return 1;
  return b.json.catalog_score - a.json.catalog_score;
});

// Assign Pages to Body
const startPage = 4;
body.forEach((item, index) => {
  const page = startPage + Math.floor(index / ITEMS_PER_PAGE);
  item.json.Layout_Section = `Page ${page} (${item.json.clean_category})`;
});

// 6. Combine and Return
const finalCatalog = [...heroes, ...body];

// Return clean list
return finalCatalog;

const results = [];

for (const item of items) {
  const html = item.json.data || '';

  let description = '';
  let productTitle = '';
  let pageStatus = '';

  // Extract product title
  const titleMatch = html.match(/<title>(.*?)<\/title>/);
  productTitle = titleMatch ? titleMatch[1].replace(' - Christianbook.com', '').trim() : '';

  // Check if we got redirected to homepage
  if (html.includes('Christian Books, Bibles, Gifts & more')) {
    pageStatus = 'HOMEPAGE_REDIRECT';
    description = 'ERROR: Bot detection - redirected to homepage';
  } else {
    pageStatus = 'PRODUCT_PAGE';

    // Try multiple methods to find the description

    // Method 1: Look for the description paragraph class
    let match = html.match(/<div[^>]*class="[^"]*CBD-ProductDetail-Description-Paragraph[^"]*"[^>]*>([\s\S]*?)<\/div>/);

    // Method 2: Look for product description meta tag
    if (!match || !match[1]) {
      match = html.match(/<meta\s+name="description"\s+content="([^"]*)"/);
    }

    // Method 3: Look for any element with product description
    if (!match || !match[1]) {
      match = html.match(/<div[^>]*class="[^"]*product-description[^"]*"[^>]*>([\s\S]*?)<\/div>/);
    }

    if (match && match[1]) {
      description = match[1]
        .replace(/<[^>]*>/g, '') // Remove HTML tags
        .replace(/&nbsp;/g, ' ')
        .replace(/&amp;/g, '&')
        .replace(/&lt;/g, '<')
        .replace(/&gt;/g, '>')
        .replace(/&quot;/g, '"')
        .replace(/&#39;/g, "'")
        .replace(/\s+/g, ' ') // Normalize whitespace
        .trim();
    } else {
      description = 'Description not found on product page';
    }
  }

  results.push({
    json: {
      SKU: item.json.SKU,
      'Promo Net Sales': item.json['Promo Net Sales'],
      'Item Description': item.json['Item Description'],
      productTitle: productTitle,
      description: description,
      pageStatus: pageStatus,
      // Keep all original fields
      ...item.json
    }
  });
}

return results;
